mapper.select:
SELECT 

FROM efm_smycmp3wjm a

WHERE a.id = #{id}

SELECT 

FROM efm_smycmp3wjm a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.proc_sno = #{procSno}


		AND a.shenqingren = #{shenqingren}


		AND a.shenqingbumen = #{shenqingbumen}


		AND a.jieshouren = #{jieshouren}


		AND a.jieshoubumen = #{jieshoubumen}


		AND a.shenqingrenmiji = #{shenqingrenmiji}


		AND a.jieshourenmiji = #{jieshourenmiji}


		AND a.sqrlxfs = #{sqrlxfs}


		AND a.shenqingshijian = #{shenqingshijian}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM efm_smycmp3wjm a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE efm_smycmp3wjm SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
