FROM efm_uubejtefnl a

WHERE a.id = #{id}

SELECT 

FROM efm_uubejtefnl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.zaitimingcheng = #{zaitimingcheng}


		AND a.zaitibianhao = #{zaitibianhao}


		AND a.miji = #{miji}


		AND a.zaitizhuangtai = #{zaitizhuangtai}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM efm_uubejtefnl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

