mapper.select:
SELECT 

FROM qm_resource_type a

WHERE a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM qm_resource_type a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE qm_resource_type SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE qm_resource_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
